function calculateProfitLoss(a){setLoadingText("Calculating Profit / Loss");var c=a.find('input[name="send-to-address"]');var b=[];$(c).each(function(){b.push($(this).val())});if(b.length==0){return}$.get(root+"walletprofitloss?input_address="+getActiveAddresses().join("|")+"&output_address="+b.join("|")+"&format=plain").success(function(e){var d=a.find(".profit-loss").show(200);d.find(".n-bets").text(e.n_sent+e.n_received);d.find(".n-pending").text(e.n_sent-e.n_received);var f=e.total_received-e.total_sent;if(f>0){d.find(".winnings").html('<font color="green">'+formatMoney(f,true)+"</font>")}else{if(f<0){d.find(".winnings").html('<font color="red">'+formatMoney(f,true)+"</font>")}else{d.find(".winnings").html(formatMoney(f,true))}}d.find(".refresh").unbind().click(function(){calculateProfitLoss(a)})})}function buildForm(b){var a=b.find(".recipient-container");if(!a.is(":empty")){return}$.get(root+"dicegames?game="+b.data("name")).success(function(c){a.empty();var d=$('<div class="control-group"><label class="control-label">Win Odds</label><div class="controls"><p>Enter the amounts you wish to bet below:</p></div></div>');a.append(d);for(var j in c){var g=c[j];if(g.popular){a.append('<div class="control-group recipient"><label class="control-label">'+g.odds+'%</label><div class="controls"><input name="send-to-address" type="hidden" value="'+g.address+'" /><div class="input-append"> <input class="send-value" style="width:auto;max-width:145px;" data-optional="true" name="send-value" data-multiplier="'+g.multiplier+'" data-minbet="'+g.minBet+'" data-maxbet="'+g.maxBet+'" placeholder="Bet Amount (BTC)" type="text" /><span class="add-on send-win-amount">No Bet</span> </div></div></div>')}}function f(e){if(e.val()>0){e.parent().find(".send-win-amount").html("Win Amount: "+(parseFloat(e.val())*parseFloat(e.data("multiplier"))).toFixed(4)+" BTC")}else{e.parent().find(".send-win-amount").html("No Bet")}}a.find('input[name="send-value"]').change(function(){if($(this).val()>$(this).data("maxbet")){$(this).val($(this).data("maxbet"));makeNotice("error","misc-error","The Maximum Bet is "+$(this).data("maxbet")+" BTC")}if($(this).val()==0){$(this).val("")}else{if($(this).val()<$(this).data("minbet")){$(this).val($(this).data("minbet"));makeNotice("error","misc-error","The Minimum Bet is "+$(this).data("minbet")+" BTC")}}f($(this))});a.find('input[name="send-value"]').keyup(function(){f($(this))});b.find(".send").unbind().click(function(){loadScript(resource+"wallet/signer.min.js",function(){startTxUI(b,"dice",initNewTx())})});try{calculateProfitLoss(b)}catch(h){console.log(h)}}).error(function(){makeNotice("error","misc-error","Error Downloading "+b.data("name")+" Bets")})};