function calculateProfitLoss(a){setLoadingText("Calculating Profit / Loss");var c=a.find('input[name="send-to-address"]');var b=[];$(c).each(function(){b.push($(this).val())});if(b.length==0){return}$.get(root+"walletprofitloss?input_address="+getActiveAddresses().join("|")+"&output_address="+b.join("|")+"&format=plain").success(function(d){var e=a.find(".profit-loss").show(200);e.find(".n-bets").text(d.n_sent+d.n_received);e.find(".n-pending").text(d.n_sent-d.n_received);var f=d.total_received-d.total_sent;if(f>0){e.find(".winnings").html('<font color="green">'+formatMoney(f,true)+"</font>")}else{if(f<0){e.find(".winnings").html('<font color="red">'+formatMoney(f,true)+"</font>")}else{e.find(".winnings").html(formatMoney(f,true))}}e.find(".refresh").unbind().click(function(){calculateProfitLoss(a)})})}function buildForm(a){var b=a.find(".recipient-container");if(!b.is(":empty")){return}$.get(root+"dicegames?game="+a.data("name")).success(function(f){b.empty();var d=$('<div class="control-group"><label class="control-label">Win Odds</label><div class="controls"><p>Enter the amounts you wish to bet below:</p></div></div>');b.append(d);for(var c in f){var g=f[c];if(g.popular){b.append('<div class="control-group recipient"><label class="control-label">'+g.odds+'%</label><div class="controls"><input name="send-to-address" type="hidden" value="'+g.address+'" /><div class="input-append"> <input class="send-value" style="width:auto;max-width:145px;" data-optional="true" name="send-value" data-multiplier="'+g.multiplier+'" data-minbet="'+g.minBet+'" data-maxbet="'+g.maxBet+'" placeholder="Bet Amount (BTC)" type="text" /><span class="add-on send-win-amount">No Bet</span> </div></div></div>')}}function h(e){if(e.val()>0){e.parent().find(".send-win-amount").html("Win Amount: "+(parseFloat(e.val())*parseFloat(e.data("multiplier"))).toFixed(4)+" BTC")}else{e.parent().find(".send-win-amount").html("No Bet")}}b.find('input[name="send-value"]').change(function(){if($(this).val()>$(this).data("maxbet")){$(this).val($(this).data("maxbet"));makeNotice("error","misc-error","The Maximum Bet is "+$(this).data("maxbet")+" BTC")}if($(this).val()==0){$(this).val("")}else{if($(this).val()<$(this).data("minbet")){$(this).val($(this).data("minbet"));makeNotice("error","misc-error","The Minimum Bet is "+$(this).data("minbet")+" BTC")}}h($(this))});b.find('input[name="send-value"]').keyup(function(){h($(this))});a.find(".send").unbind().click(function(){loadScript(resource+"wallet/signer.min.js",function(){startTxUI(a,"dice",initNewTx())})});try{calculateProfitLoss(a)}catch(j){console.log(j)}}).error(function(){makeNotice("error","misc-error","Error Downloading "+a.data("name")+" Bets")})};