function calculateProfitLoss(c){setLoadingText("Calculating Profit / Loss");var a=c.find('input[name="send-to-address"]');var b=[];$(a).each(function(){b.push($(this).val())});if(b.length==0){return}$.get(root+"walletprofitloss?input_address="+getActiveAddresses().join("|")+"&output_address="+b.join("|")+"&format=plain").success(function(d){var f=c.find(".profit-loss").show(200);f.find(".n-bets").text(d.n_sent+d.n_received);f.find(".n-pending").text(d.n_sent-d.n_received);var e=d.total_received-d.total_sent;if(e>0){f.find(".winnings").html('<font color="green">'+formatMoney(e,true)+"</font>")}else{if(e<0){f.find(".winnings").html('<font color="red">'+formatMoney(e,true)+"</font>")}else{f.find(".winnings").html(formatMoney(e,true))}}f.find(".refresh").unbind().click(function(){calculateProfitLoss(c)})})}function buildForm(b){var a=b.find(".recipient-container");if(!a.is(":empty")){return}$.get(root+"dicegames?game="+b.data("name")).success(function(c){a.empty();var f=$('<div class="control-group"><label class="control-label">Win Odds</label><div class="controls"><p>Enter the amounts you wish to bet below:</p></div></div>');a.append(f);for(var d in c){var j=c[d];if(j.popular){a.append('<div class="control-group recipient"><label class="control-label">'+j.odds+'%</label><div class="controls"><input name="send-to-address" type="hidden" value="'+j.address+'" /><div class="input-append"> <input class="send-value" style="width:auto;max-width:145px;" data-optional="true" name="send-value" data-multiplier="'+j.multiplier+'" data-minbet="'+j.minBet+'" data-maxbet="'+j.maxBet+'" placeholder="Bet Amount (BTC)" type="text" /><span class="add-on send-win-amount">No Bet</span> </div></div></div>')}}function h(e){if(e.val()>0){e.parent().find(".send-win-amount").html("Win Amount: "+(parseFloat(e.val())*parseFloat(e.data("multiplier"))).toFixed(4)+" BTC")}else{e.parent().find(".send-win-amount").html("No Bet")}}a.find('input[name="send-value"]').change(function(){if($(this).val()>$(this).data("maxbet")){$(this).val($(this).data("maxbet"));makeNotice("error","misc-error","The Maximum Bet is "+$(this).data("maxbet")+" BTC")}if($(this).val()==0){$(this).val("")}else{if($(this).val()<$(this).data("minbet")){$(this).val($(this).data("minbet"));makeNotice("error","misc-error","The Minimum Bet is "+$(this).data("minbet")+" BTC")}}h($(this))});a.find('input[name="send-value"]').keyup(function(){h($(this))});b.find(".send").unbind().click(function(){loadScript(resource+"wallet/signer.min.js",function(){startTxUI(b,"dice",initNewTx())})});try{calculateProfitLoss(b)}catch(g){console.log(g)}}).error(function(){makeNotice("error","misc-error","Error Downloading "+b.data("name")+" Bets")})};