$(document).ready(function(){var a=$(document.body).data("ip");webSocketConnect(function(b){b.onmessage=function(h){var g=$.parseJSON(h.data);if(g.op=="status"){$("#status").html(g.msg)}else{if(g.op=="utx"){op=g.x;playSound("beep");var d=TransactionFromJSON(op);var f=$(d.getHTML());var c=$("#tx_container");c.prepend(f);setupSymbolToggle();f.hide().slideDown("slow");if(c.find(".txdiv").length>50){c.find(".txdiv:last-child").remove()}$("#n_transactions").text(parseInt($("#n_transactions").text())+1);calcMoney()}}};b.onopen=function(){$("#status").html("Connected. ");b.send('{"op":"ip_sub", "ip":"'+a+'"}')};b.onclose=function(){$("#status").html("Disconnected")}})});