function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var k=$("#active-addresses");var l=k.find("table");l.find("tbody").remove();for(var j in m){var b=m[j];if(j==0){l.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(l);for(var a in b){var d=b[a];var e=addresses[d];if(!e){continue}var n="";if(e.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var h=e.addr;if(e.label!=null){h=e.label;g='<span class="hidden-phone"> - '+e.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+h+"</a>"+g+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(c)}l.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var l=[];var d=c.filteredTaints;for(var f in d){var n=d[f];var h=-1;for(var e in n){var g=n[e];for(var k in l){if(k!=h&&$.inArray(e,l[k])){if(h>=0){var m=l.splice(h,1);var j=l.splice(k,1);l.push(m.concat(j))}else{l[k].push(f)}h=k}if(h>=0){break}}}if(h==-1){l.push([f])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};