function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var b=$("#active-addresses");var l=b.find("table");l.find("tbody").remove();for(var f in j){var k=j[f];if(f==0){l.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(l);for(var g in k){var m=k[g];var n=addresses[m];if(!n){continue}var a="";if(n.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var e=n.addr;if(n.label!=null){e=n.label;h='<span class="hidden-phone"> - '+n.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+e+"</a>"+h+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(c)}l.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var k=[];var d=l.filteredTaints;for(var n in d){var g=d[n];var j=-1;for(var f in g){var m=g[f];for(var h in k){if(h!=j&&$.inArray(f,k[h])){if(j>=0){var e=k.splice(j,1);var c=k.splice(h,1);k.push(e.concat(c))}else{k[h].push(n)}j=h}if(j>=0){break}}}if(j==-1){k.push([n])}}buildTable(k);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};