function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var n=$("#active-addresses");var e=n.find("table");e.find("tbody").remove();for(var j in c){var k=c[j];if(j==0){e.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(e);for(var f in k){var b=k[f];var m=addresses[b];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var d=m.addr;if(m.label!=null){d=m.label;a='<span class="hidden-phone"> - '+m.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+d+"</a>"+a+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(l)}e.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var e=[];var n=f.filteredTaints;for(var d in n){var c=n[d];var k=-1;for(var h in c){var l=c[h];for(var m in e){if(m!=k&&$.inArray(h,e[m])){if(k>=0){var g=e.splice(k,1);var j=e.splice(m,1);e.push(g.concat(j))}else{e[m].push(d)}k=m}if(k>=0){break}}}if(k==-1){e.push([d])}}buildTable(e);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};