function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var g=$("#active-addresses");var d=g.find("table");d.find("tbody").remove();for(var c in e){var k=e[c];if(c==0){d.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(d);for(var l in k){var j=k[l];var a=addresses[j];if(!a){continue}var f="";if(a.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var h=a.addr;if(a.label!=null){h=a.label;b='<span class="hidden-phone"> - '+a.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+h+"</a>"+b+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(m)}d.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var j=[];var h=l.filteredTaints;for(var m in h){var d=h[m];var n=-1;for(var k in d){var e=d[k];for(var g in j){if(g!=n&&$.inArray(k,j[g])){if(n>=0){var c=j.splice(n,1);var f=j.splice(g,1);j.push(c.concat(f))}else{j[g].push(m)}n=g}if(n>=0){break}}}if(n==-1){j.push([m])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};