function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var m=$("#active-addresses");var k=m.find("table");k.find("tbody").remove();for(var a in l){var c=l[a];if(a==0){k.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(k);for(var h in c){var d=c[h];var g=addresses[d];if(!g){continue}var n="";if(g.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var j=g.addr;if(g.label!=null){j=g.label;f='<span class="hidden-phone"> - '+g.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+j+"</a>"+f+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(b)}k.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var l=[];var h=n.filteredTaints;for(var d in h){var k=h[d];var f=-1;for(var e in k){var g=k[e];for(var c in l){if(c!=f&&$.inArray(e,l[c])){if(f>=0){var j=l.splice(f,1);var m=l.splice(c,1);l.push(j.concat(m))}else{l[c].push(d)}f=c}if(f>=0){break}}}if(f==-1){l.push([d])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};