function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var c=$("#active-addresses");var h=c.find("table");h.find("tbody").remove();for(var k in j){var e=j[k];if(k==0){h.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(h);for(var l in e){var b=e[l];var f=addresses[b];if(!f){continue}var g="";if(f.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var m=f.addr;if(f.label!=null){m=f.label;n='<span class="hidden-phone"> - '+f.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+m+"</a>"+n+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(a)}h.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var h=[];var n=l.filteredTaints;for(var d in n){var m=n[d];var c=-1;for(var j in m){var e=m[j];for(var k in h){if(k!=c&&$.inArray(j,h[k])){if(c>=0){var g=h.splice(c,1);var f=h.splice(k,1);h.push(g.concat(f))}else{h[k].push(d)}c=k}if(c>=0){break}}}if(c==-1){h.push([d])}}buildTable(h);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};