function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var c=$("#active-addresses");var b=c.find("table");b.find("tbody").remove();for(var j in a){var k=a[j];if(j==0){b.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(b);for(var e in k){var d=k[e];var h=addresses[d];if(!h){continue}var n="";if(h.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var m=h.addr;if(h.label!=null){m=h.label;g='<span class="hidden-phone"> - '+h.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+m+"</a>"+g+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(l)}b.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var c=[];var n=g.filteredTaints;for(var f in n){var d=n[f];var e=-1;for(var h in d){var k=d[h];for(var m in c){if(m!=e&&$.inArray(h,c[m])){if(e>=0){var j=c.splice(e,1);var l=c.splice(m,1);c.push(j.concat(l))}else{c[m].push(f)}e=m}if(e>=0){break}}}if(e==-1){c.push([f])}}buildTable(c);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};