function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var j=$("#active-addresses");var f=j.find("table");f.find("tbody").remove();for(var g in d){var k=d[g];if(g==0){f.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(f);for(var a in k){var e=k[a];var b=addresses[e];if(!b){continue}var l="";if(b.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var m=b.addr;if(b.label!=null){m=b.label;n='<span class="hidden-phone"> - '+b.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+m+"</a>"+n+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(h)}f.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var e=[];var g=n.filteredTaints;for(var f in g){var l=g[f];var h=-1;for(var c in l){var m=l[c];for(var j in e){if(j!=h&&$.inArray(c,e[j])){if(h>=0){var d=e.splice(h,1);var k=e.splice(j,1);e.push(d.concat(k))}else{e[j].push(f)}h=j}if(h>=0){break}}}if(h==-1){e.push([f])}}buildTable(e);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};