function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var g=$("#active-addresses");var n=g.find("table");n.find("tbody").remove();for(var b in m){var c=m[b];if(b==0){n.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(n);for(var d in c){var a=c[d];var l=addresses[a];if(!l){continue}var k="";if(l.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var f=l.addr;if(l.label!=null){f=l.label;h='<span class="hidden-phone"> - '+l.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+f+"</a>"+h+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(e)}n.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var n=[];var l=e.filteredTaints;for(var d in l){var j=l[d];var k=-1;for(var m in j){var h=j[m];for(var f in n){if(f!=k&&$.inArray(m,n[f])){if(k>=0){var c=n.splice(k,1);var g=n.splice(f,1);n.push(c.concat(g))}else{n[f].push(d)}k=f}if(k>=0){break}}}if(k==-1){n.push([d])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};