function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var n=$("#active-addresses");var e=n.find("table");e.find("tbody").remove();for(var k in a){var f=a[k];if(k==0){e.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(e);for(var j in f){var c=f[j];var h=addresses[c];if(!h){continue}var m="";if(h.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var l=h.addr;if(h.label!=null){l=h.label;g='<span class="hidden-phone"> - '+h.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+l+"</a>"+g+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(b)}e.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var c=[];var f=l.filteredTaints;for(var d in f){var j=f[d];var g=-1;for(var m in j){var k=j[m];for(var h in c){if(h!=g&&$.inArray(m,c[h])){if(g>=0){var e=c.splice(g,1);var n=c.splice(h,1);c.push(e.concat(n))}else{c[h].push(d)}g=h}if(g>=0){break}}}if(g==-1){c.push([d])}}buildTable(c);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};