function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var a=$("#active-addresses");var d=a.find("table");d.find("tbody").remove();for(var n in f){var e=f[n];if(n==0){d.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(d);for(var k in e){var g=e[k];var c=addresses[g];if(!c){continue}var j="";if(c.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var m=c.addr;if(c.label!=null){m=c.label;h='<span class="hidden-phone"> - '+c.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+m+"</a>"+h+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(b)}d.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var f=[];var d=n.filteredTaints;for(var h in d){var c=d[h];var l=-1;for(var g in c){var e=c[g];for(var m in f){if(m!=l&&$.inArray(g,f[m])){if(l>=0){var j=f.splice(l,1);var k=f.splice(m,1);f.push(j.concat(k))}else{f[m].push(h)}l=m}if(l>=0){break}}}if(l==-1){f.push([h])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};