function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var n=$("#active-addresses");var f=n.find("table");f.find("tbody").remove();for(var l in d){var a=d[l];if(l==0){f.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(f);for(var g in a){var e=a[g];var j=addresses[e];if(!j){continue}var h="";if(j.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var c=j.addr;if(j.label!=null){c=j.label;b='<span class="hidden-phone"> - '+j.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+c+"</a>"+b+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(k)}f.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var d=[];var h=c.filteredTaints;for(var f in h){var l=h[f];var g=-1;for(var e in l){var m=l[e];for(var n in d){if(n!=g&&$.inArray(e,d[n])){if(g>=0){var k=d.splice(g,1);var j=d.splice(n,1);d.push(k.concat(j))}else{d[n].push(f)}g=n}if(g>=0){break}}}if(g==-1){d.push([f])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};