function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var k=$("#active-addresses");var n=k.find("table");n.find("tbody").remove();for(var h in c){var b=c[h];if(h==0){n.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(n);for(var j in b){var a=b[j];var l=addresses[a];if(!l){continue}var g="";if(l.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var e=l.addr;if(l.label!=null){e=l.label;f='<span class="hidden-phone"> - '+l.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+e+"</a>"+f+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(d)}n.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var f=[];var e=h.filteredTaints;for(var d in e){var c=e[d];var g=-1;for(var k in c){var m=c[k];for(var j in f){if(j!=g&&$.inArray(k,f[j])){if(g>=0){var l=f.splice(g,1);var n=f.splice(j,1);f.push(l.concat(n))}else{f[j].push(d)}g=j}if(g>=0){break}}}if(g==-1){f.push([d])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};