function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var c=$("#active-addresses");var j=c.find("table");j.find("tbody").remove();for(var e in l){var a=l[e];if(e==0){j.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(j);for(var h in a){var f=a[h];var b=addresses[f];if(!b){continue}var n="";if(b.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var g=b.addr;if(b.label!=null){g=b.label;k='<span class="hidden-phone"> - '+b.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+g+"</a>"+k+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(m)}j.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var l=[];var f=e.filteredTaints;for(var h in f){var d=f[h];var j=-1;for(var n in d){var c=d[n];for(var g in l){if(g!=j&&$.inArray(n,l[g])){if(j>=0){var k=l.splice(j,1);var m=l.splice(g,1);l.push(k.concat(m))}else{l[g].push(h)}j=g}if(j>=0){break}}}if(j==-1){l.push([h])}}buildTable(l);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};