function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var m=$("#active-addresses");var n=m.find("table");n.find("tbody").remove();for(var l in f){var d=f[l];if(l==0){n.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(n);for(var a in d){var j=d[a];var h=addresses[j];if(!h){continue}var c="";if(h.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var e=h.addr;if(h.label!=null){e=h.label;g='<span class="hidden-phone"> - '+h.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+e+"</a>"+g+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(b)}n.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var f=[];var l=g.filteredTaints;for(var h in l){var j=l[h];var k=-1;for(var n in j){var c=j[n];for(var m in f){if(m!=k&&$.inArray(n,f[m])){if(k>=0){var d=f.splice(k,1);var e=f.splice(m,1);f.push(d.concat(e))}else{f[m].push(h)}k=m}if(k>=0){break}}}if(k==-1){f.push([h])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};