function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var d=$("#active-addresses");var f=d.find("table");f.find("tbody").remove();for(var m in l){var n=l[m];if(m==0){f.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(f);for(var k in n){var a=n[k];var g=addresses[a];if(!g){continue}var b="";if(g.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var e=g.addr;if(g.label!=null){e=g.label;c='<span class="hidden-phone"> - '+g.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+e+"</a>"+c+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(j)}f.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var m=[];var e=g.filteredTaints;for(var c in e){var d=e[c];var j=-1;for(var n in d){var h=d[n];for(var l in m){if(l!=j&&$.inArray(n,m[l])){if(j>=0){var f=m.splice(j,1);var k=m.splice(l,1);m.push(f.concat(k))}else{m[l].push(c)}j=l}if(j>=0){break}}}if(j==-1){m.push([c])}}buildTable(m);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};