function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var d=$("#active-addresses");var f=d.find("table");f.find("tbody").remove();for(var g in k){var n=k[g];if(g==0){f.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(f);for(var e in n){var a=n[e];var m=addresses[a];if(!m){continue}var c="";if(m.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var h=m.addr;if(m.label!=null){h=m.label;j='<span class="hidden-phone"> - '+m.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+h+"</a>"+j+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(b)}f.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var l=[];var d=j.filteredTaints;for(var c in d){var m=d[c];var f=-1;for(var k in m){var h=m[k];for(var g in l){if(g!=f&&$.inArray(k,l[g])){if(f>=0){var e=l.splice(f,1);var n=l.splice(g,1);l.push(e.concat(n))}else{l[g].push(c)}f=g}if(f>=0){break}}}if(f==-1){l.push([c])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};