function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var n=$("#active-addresses");var h=n.find("table");h.find("tbody").remove();for(var g in b){var m=b[g];if(g==0){h.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(h);for(var c in m){var j=m[c];var l=addresses[j];if(!l){continue}var e="";if(l.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var d=l.addr;if(l.label!=null){d=l.label;f='<span class="hidden-phone"> - '+l.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+d+"</a>"+f+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(k)}h.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var c=[];var d=h.filteredTaints;for(var m in d){var k=d[m];var n=-1;for(var j in k){var f=k[j];for(var l in c){if(l!=n&&$.inArray(j,c[l])){if(n>=0){var e=c.splice(n,1);var g=c.splice(l,1);c.push(e.concat(g))}else{c[l].push(m)}n=l}if(n>=0){break}}}if(n==-1){c.push([m])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};