function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var a=$("#active-addresses");var g=a.find("table");g.find("tbody").remove();for(var j in m){var e=m[j];if(j==0){g.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(g);for(var n in e){var d=e[n];var c=addresses[d];if(!c){continue}var k="";if(c.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var l=c.addr;if(c.label!=null){l=c.label;h='<span class="hidden-phone"> - '+c.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+l+"</a>"+h+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(f)}g.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var m=[];var c=h.filteredTaints;for(var d in c){var k=c[d];var f=-1;for(var j in k){var e=k[j];for(var g in m){if(g!=f&&$.inArray(j,m[g])){if(f>=0){var n=m.splice(f,1);var l=m.splice(g,1);m.push(n.concat(l))}else{m[g].push(d)}f=g}if(f>=0){break}}}if(f==-1){m.push([d])}}buildTable(m);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};