function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var k=$("#active-addresses");var m=k.find("table");m.find("tbody").remove();for(var g in n){var j=n[g];if(g==0){m.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(m);for(var a in j){var d=j[a];var c=addresses[d];if(!c){continue}var b="";if(c.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var e=c.addr;if(c.label!=null){e=c.label;l='<span class="hidden-phone"> - '+c.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+e+"</a>"+l+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(f)}m.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var n=[];var f=l.filteredTaints;for(var e in f){var k=f[e];var j=-1;for(var h in k){var m=k[h];for(var g in n){if(g!=j&&$.inArray(h,n[g])){if(j>=0){var c=n.splice(j,1);var d=n.splice(g,1);n.push(c.concat(d))}else{n[g].push(e)}j=g}if(j>=0){break}}}if(j==-1){n.push([e])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};