function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var f=$("#active-addresses");var l=f.find("table");l.find("tbody").remove();for(var n in d){var h=d[n];if(n==0){l.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(l);for(var g in h){var k=h[g];var b=addresses[k];if(!b){continue}var c="";if(b.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var e=b.addr;if(b.label!=null){e=b.label;m='<span class="hidden-phone"> - '+b.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+e+"</a>"+m+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(j)}l.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var g=[];var f=d.filteredTaints;for(var k in f){var l=f[k];var h=-1;for(var j in l){var m=l[j];for(var n in g){if(n!=h&&$.inArray(j,g[n])){if(h>=0){var e=g.splice(h,1);var c=g.splice(n,1);g.push(e.concat(c))}else{g[n].push(k)}h=n}if(h>=0){break}}}if(h==-1){g.push([k])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};