function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var m=$("#active-addresses");var b=m.find("table");b.find("tbody").remove();for(var h in f){var j=f[h];if(h==0){b.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(b);for(var g in j){var d=j[g];var n=addresses[d];if(!n){continue}var l="";if(n.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var a=n.addr;if(n.label!=null){a=n.label;c='<span class="hidden-phone"> - '+n.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+a+"</a>"+c+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(k)}b.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var g=[];var l=d.filteredTaints;for(var e in l){var m=l[e];var f=-1;for(var n in m){var c=m[n];for(var k in g){if(k!=f&&$.inArray(n,g[k])){if(f>=0){var j=g.splice(f,1);var h=g.splice(k,1);g.push(j.concat(h))}else{g[k].push(e)}f=k}if(f>=0){break}}}if(f==-1){g.push([e])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};