function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var h=$("#active-addresses");var e=h.find("table");e.find("tbody").remove();for(var l in b){var m=b[l];if(l==0){e.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(e);for(var g in m){var n=m[g];var c=addresses[n];if(!c){continue}var f="";if(c.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var j=c.addr;if(c.label!=null){j=c.label;a='<span class="hidden-phone"> - '+c.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+j+"</a>"+a+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(k)}e.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var d=[];var j=c.filteredTaints;for(var n in j){var g=j[n];var l=-1;for(var h in g){var f=g[h];for(var m in d){if(m!=l&&$.inArray(h,d[m])){if(l>=0){var e=d.splice(l,1);var k=d.splice(m,1);d.push(e.concat(k))}else{d[m].push(n)}l=m}if(l>=0){break}}}if(l==-1){d.push([n])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};