function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var l=$("#active-addresses");var e=l.find("table");e.find("tbody").remove();for(var d in g){var k=g[d];if(d==0){e.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(e);for(var h in k){var n=k[h];var f=addresses[n];if(!f){continue}var b="";if(f.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var m=f.addr;if(f.label!=null){m=f.label;c='<span class="hidden-phone"> - '+f.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+m+"</a>"+c+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(a)}e.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var j=[];var e=g.filteredTaints;for(var n in e){var l=e[n];var h=-1;for(var k in l){var m=l[k];for(var d in j){if(d!=h&&$.inArray(k,j[d])){if(h>=0){var f=j.splice(h,1);var c=j.splice(d,1);j.push(f.concat(c))}else{j[d].push(n)}h=d}if(h>=0){break}}}if(h==-1){j.push([n])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};