function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var m=$("#active-addresses");var h=m.find("table");h.find("tbody").remove();for(var n in c){var j=c[n];if(n==0){h.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(h);for(var f in j){var k=j[f];var a=addresses[k];if(!a){continue}var b="";if(a.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var e=a.addr;if(a.label!=null){e=a.label;g='<span class="hidden-phone"> - '+a.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+e+"</a>"+g+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(l)}h.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var c=[];var e=m.filteredTaints;for(var k in e){var g=e[k];var d=-1;for(var l in g){var h=g[l];for(var n in c){if(n!=d&&$.inArray(l,c[n])){if(d>=0){var f=c.splice(d,1);var j=c.splice(n,1);c.push(f.concat(j))}else{c[n].push(k)}d=n}if(d>=0){break}}}if(d==-1){c.push([k])}}buildTable(c);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};