function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var n=$("#active-addresses");var a=n.find("table");a.find("tbody").remove();for(var f in h){var d=h[f];if(f==0){a.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(a);for(var b in d){var c=d[b];var l=addresses[c];if(!l){continue}var k="";if(l.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var m=l.addr;if(l.label!=null){m=l.label;e='<span class="hidden-phone"> - '+l.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+m+"</a>"+e+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(g)}a.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var l=[];var m=j.filteredTaints;for(var e in m){var k=m[e];var n=-1;for(var c in k){var f=k[c];for(var h in l){if(h!=n&&$.inArray(c,l[h])){if(n>=0){var g=l.splice(n,1);var d=l.splice(h,1);l.push(g.concat(d))}else{l[h].push(e)}n=h}if(n>=0){break}}}if(n==-1){l.push([e])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};