function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var d=$("#active-addresses");var b=d.find("table");b.find("tbody").remove();for(var j in g){var e=g[j];if(j==0){b.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(b);for(var f in e){var k=e[f];var h=addresses[k];if(!h){continue}var a="";if(h.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var l=h.addr;if(h.label!=null){l=h.label;c='<span class="hidden-phone"> - '+h.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+l+"</a>"+c+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(m)}b.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var d=[];var g=l.filteredTaints;for(var f in g){var m=g[f];var h=-1;for(var c in m){var n=m[c];for(var e in d){if(e!=h&&$.inArray(c,d[e])){if(h>=0){var k=d.splice(h,1);var j=d.splice(e,1);d.push(k.concat(j))}else{d[e].push(f)}h=e}if(h>=0){break}}}if(h==-1){d.push([f])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};