function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var d=$("#active-addresses");var l=d.find("table");l.find("tbody").remove();for(var f in e){var n=e[f];if(f==0){l.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(l);for(var a in n){var b=n[a];var c=addresses[b];if(!c){continue}var g="";if(c.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var j=c.addr;if(c.label!=null){j=c.label;k='<span class="hidden-phone"> - '+c.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+j+"</a>"+k+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(m)}l.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var e=[];var l=n.filteredTaints;for(var d in l){var k=l[d];var h=-1;for(var j in k){var f=k[j];for(var g in e){if(g!=h&&$.inArray(j,e[g])){if(h>=0){var m=e.splice(h,1);var c=e.splice(g,1);e.push(m.concat(c))}else{e[g].push(d)}h=g}if(h>=0){break}}}if(h==-1){e.push([d])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};