function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var g=$("#active-addresses");var j=g.find("table");j.find("tbody").remove();for(var e in f){var m=f[e];if(e==0){j.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(j);for(var h in m){var k=m[h];var d=addresses[k];if(!d){continue}var b="";if(d.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var a=d.addr;if(d.label!=null){a=d.label;l='<span class="hidden-phone"> - '+d.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+a+"</a>"+l+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(n)}j.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var h=[];var n=l.filteredTaints;for(var m in n){var c=n[m];var d=-1;for(var k in c){var f=c[k];for(var g in h){if(g!=d&&$.inArray(k,h[g])){if(d>=0){var e=h.splice(d,1);var j=h.splice(g,1);h.push(e.concat(j))}else{h[g].push(m)}d=g}if(d>=0){break}}}if(d==-1){h.push([m])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};