function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var b=$("#active-addresses");var k=b.find("table");k.find("tbody").remove();for(var d in j){var n=j[d];if(d==0){k.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(k);for(var g in n){var a=n[g];var h=addresses[a];if(!h){continue}var m="";if(h.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var c=h.addr;if(h.label!=null){c=h.label;f='<span class="hidden-phone"> - '+h.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+c+"</a>"+f+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(l)}k.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var l=[];var j=k.filteredTaints;for(var e in j){var n=j[e];var m=-1;for(var h in n){var c=n[h];for(var g in l){if(g!=m&&$.inArray(h,l[g])){if(m>=0){var d=l.splice(m,1);var f=l.splice(g,1);l.push(d.concat(f))}else{l[g].push(e)}m=g}if(m>=0){break}}}if(m==-1){l.push([e])}}buildTable(l);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};