function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var m=$("#active-addresses");var b=m.find("table");b.find("tbody").remove();for(var d in c){var g=c[d];if(d==0){b.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(b);for(var j in g){var f=g[j];var k=addresses[f];if(!k){continue}var e="";if(k.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var a=k.addr;if(k.label!=null){a=k.label;h='<span class="hidden-phone"> - '+k.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+a+"</a>"+h+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(l)}b.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var d=[];var l=n.filteredTaints;for(var h in l){var m=l[h];var j=-1;for(var k in m){var c=m[k];for(var e in d){if(e!=j&&$.inArray(k,d[e])){if(j>=0){var f=d.splice(j,1);var g=d.splice(e,1);d.push(f.concat(g))}else{d[e].push(h)}j=e}if(j>=0){break}}}if(j==-1){d.push([h])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};