function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var f=$("#active-addresses");var j=f.find("table");j.find("tbody").remove();for(var b in e){var c=e[b];if(b==0){j.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(j);for(var h in c){var d=c[h];var l=addresses[d];if(!l){continue}var n="";if(l.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var a=l.addr;if(l.label!=null){a=l.label;m='<span class="hidden-phone"> - '+l.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+a+"</a>"+m+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(g)}j.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var f=[];var h=g.filteredTaints;for(var e in h){var k=h[e];var j=-1;for(var l in k){var n=k[l];for(var d in f){if(d!=j&&$.inArray(l,f[d])){if(j>=0){var c=f.splice(j,1);var m=f.splice(d,1);f.push(c.concat(m))}else{f[d].push(e)}j=d}if(j>=0){break}}}if(j==-1){f.push([e])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};