function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var f=$("#active-addresses");var h=f.find("table");h.find("tbody").remove();for(var k in e){var j=e[k];if(k==0){h.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(h);for(var d in j){var n=j[d];var a=addresses[n];if(!a){continue}var l="";if(a.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var g=a.addr;if(a.label!=null){g=a.label;c='<span class="hidden-phone"> - '+a.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+g+"</a>"+c+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(m)}h.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var e=[];var c=d.filteredTaints;for(var n in c){var h=c[n];var f=-1;for(var l in h){var m=h[l];for(var k in e){if(k!=f&&$.inArray(l,e[k])){if(f>=0){var g=e.splice(f,1);var j=e.splice(k,1);e.push(g.concat(j))}else{e[k].push(n)}f=k}if(f>=0){break}}}if(f==-1){e.push([n])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};