function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var n=$("#active-addresses");var e=n.find("table");e.find("tbody").remove();for(var l in f){var k=f[l];if(l==0){e.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(e);for(var a in k){var b=k[a];var d=addresses[b];if(!d){continue}var c="";if(d.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var m=d.addr;if(d.label!=null){m=d.label;h='<span class="hidden-phone"> - '+d.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+m+"</a>"+h+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(g)}e.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var d=[];var g=l.filteredTaints;for(var c in g){var j=g[c];var n=-1;for(var e in j){var f=j[e];for(var k in d){if(k!=n&&$.inArray(e,d[k])){if(n>=0){var h=d.splice(n,1);var m=d.splice(k,1);d.push(h.concat(m))}else{d[k].push(c)}n=k}if(n>=0){break}}}if(n==-1){d.push([c])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};