function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var c=$("#active-addresses");var l=c.find("table");l.find("tbody").remove();for(var n in d){var e=d[n];if(n==0){l.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(l);for(var h in e){var b=e[h];var m=addresses[b];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var f=m.addr;if(m.label!=null){f=m.label;k='<span class="hidden-phone"> - '+m.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+f+"</a>"+k+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(a)}l.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var f=[];var k=m.filteredTaints;for(var c in k){var d=k[c];var h=-1;for(var n in d){var j=d[n];for(var l in f){if(l!=h&&$.inArray(n,f[l])){if(h>=0){var e=f.splice(h,1);var g=f.splice(l,1);f.push(e.concat(g))}else{f[l].push(c)}h=l}if(h>=0){break}}}if(h==-1){f.push([c])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};