function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var a=$("#active-addresses");var f=a.find("table");f.find("tbody").remove();for(var e in g){var h=g[e];if(e==0){f.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(f);for(var b in h){var l=h[b];var c=addresses[l];if(!c){continue}var n="";if(c.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var m=c.addr;if(c.label!=null){m=c.label;j='<span class="hidden-phone"> - '+c.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+m+"</a>"+j+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(d)}f.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var h=[];var f=g.filteredTaints;for(var l in f){var n=f[l];var d=-1;for(var m in n){var c=n[m];for(var e in h){if(e!=d&&$.inArray(m,h[e])){if(d>=0){var j=h.splice(d,1);var k=h.splice(e,1);h.push(j.concat(k))}else{h[e].push(l)}d=e}if(d>=0){break}}}if(d==-1){h.push([l])}}buildTable(h);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};