function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var g=$("#active-addresses");var n=g.find("table");n.find("tbody").remove();for(var d in l){var e=l[d];if(d==0){n.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(n);for(var b in e){var m=e[b];var f=addresses[m];if(!f){continue}var a="";if(f.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var k=f.addr;if(f.label!=null){k=f.label;c='<span class="hidden-phone"> - '+f.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+k+"</a>"+c+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(j)}n.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var k=[];var n=g.filteredTaints;for(var m in n){var j=n[m];var h=-1;for(var f in j){var e=j[f];for(var c in k){if(c!=h&&$.inArray(f,k[c])){if(h>=0){var d=k.splice(h,1);var l=k.splice(c,1);k.push(d.concat(l))}else{k[c].push(m)}h=c}if(h>=0){break}}}if(h==-1){k.push([m])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};