function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var n=$("#active-addresses");var d=n.find("table");d.find("tbody").remove();for(var h in a){var m=a[h];if(h==0){d.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(d);for(var c in m){var f=m[c];var k=addresses[f];if(!k){continue}var b="";if(k.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var j=k.addr;if(k.label!=null){j=k.label;e='<span class="hidden-phone"> - '+k.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+j+"</a>"+e+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(l)}d.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var d=[];var k=l.filteredTaints;for(var m in k){var h=k[m];var f=-1;for(var c in h){var e=h[c];for(var n in d){if(n!=f&&$.inArray(c,d[n])){if(f>=0){var g=d.splice(f,1);var j=d.splice(n,1);d.push(g.concat(j))}else{d[n].push(m)}f=n}if(f>=0){break}}}if(f==-1){d.push([m])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};