function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var c=$("#active-addresses");var l=c.find("table");l.find("tbody").remove();for(var g in e){var f=e[g];if(g==0){l.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(l);for(var h in f){var d=f[h];var k=addresses[d];if(!k){continue}var m="";if(k.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var n=k.addr;if(k.label!=null){n=k.label;j='<span class="hidden-phone"> - '+k.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+n+"</a>"+j+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(a)}l.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var e=[];var f=n.filteredTaints;for(var d in f){var m=f[d];var j=-1;for(var g in m){var k=m[g];for(var c in e){if(c!=j&&$.inArray(g,e[c])){if(j>=0){var l=e.splice(j,1);var h=e.splice(c,1);e.push(l.concat(h))}else{e[c].push(d)}j=c}if(j>=0){break}}}if(j==-1){e.push([d])}}buildTable(e);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};