function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var c=$("#active-addresses");var l=c.find("table");l.find("tbody").remove();for(var j in b){var a=b[j];if(j==0){l.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(l);for(var m in a){var h=a[m];var k=addresses[h];if(!k){continue}var g="";if(k.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var f=k.addr;if(k.label!=null){f=k.label;n='<span class="hidden-phone"> - '+k.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+f+"</a>"+n+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(d)}l.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var d=[];var h=n.filteredTaints;for(var j in h){var m=h[j];var f=-1;for(var c in m){var l=m[c];for(var k in d){if(k!=f&&$.inArray(c,d[k])){if(f>=0){var g=d.splice(f,1);var e=d.splice(k,1);d.push(g.concat(e))}else{d[k].push(j)}f=k}if(f>=0){break}}}if(f==-1){d.push([j])}}buildTable(d);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};