function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var d=$("#active-addresses");var m=d.find("table");m.find("tbody").remove();for(var b in n){var e=n[b];if(b==0){m.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(m);for(var h in e){var l=e[h];var j=addresses[l];if(!j){continue}var g="";if(j.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var a=j.addr;if(j.label!=null){a=j.label;c='<span class="hidden-phone"> - '+j.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+a+"</a>"+c+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(k)}m.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var j=[];var m=e.filteredTaints;for(var h in m){var g=m[h];var d=-1;for(var f in g){var n=g[f];for(var c in j){if(c!=d&&$.inArray(f,j[c])){if(d>=0){var k=j.splice(d,1);var l=j.splice(c,1);j.push(k.concat(l))}else{j[c].push(h)}d=c}if(d>=0){break}}}if(d==-1){j.push([h])}}buildTable(j);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};