function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var a=$("#active-addresses");var b=a.find("table");b.find("tbody").remove();for(var n in h){var j=h[n];if(n==0){b.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(b);for(var d in j){var g=j[d];var l=addresses[g];if(!l){continue}var m="";if(l.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var c=l.addr;if(l.label!=null){c=l.label;k='<span class="hidden-phone"> - '+l.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+c+"</a>"+k+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(f)}b.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var h=[];var f=c.filteredTaints;for(var g in f){var l=f[g];var d=-1;for(var j in l){var k=l[j];for(var n in h){if(n!=d&&$.inArray(j,h[n])){if(d>=0){var e=h.splice(d,1);var m=h.splice(n,1);h.push(e.concat(m))}else{h[n].push(g)}d=n}if(d>=0){break}}}if(d==-1){h.push([g])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};