function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var j=$("#active-addresses");var m=j.find("table");m.find("tbody").remove();for(var e in n){var h=n[e];if(e==0){m.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(m);for(var l in h){var c=h[l];var g=addresses[c];if(!g){continue}var a="";if(g.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var d=g.addr;if(g.label!=null){d=g.label;f='<span class="hidden-phone"> - '+g.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+d+"</a>"+f+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(k)}m.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var l=[];var e=n.filteredTaints;for(var g in e){var m=e[g];var d=-1;for(var k in m){var h=m[k];for(var j in l){if(j!=d&&$.inArray(k,l[j])){if(d>=0){var c=l.splice(d,1);var f=l.splice(j,1);l.push(c.concat(f))}else{l[j].push(g)}d=j}if(d>=0){break}}}if(d==-1){l.push([g])}}buildTable(l);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};