function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var f=$("#active-addresses");var m=f.find("table");m.find("tbody").remove();for(var j in g){var d=g[j];if(j==0){m.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(m);for(var e in d){var n=d[e];var h=addresses[n];if(!h){continue}var k="";if(h.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var l=h.addr;if(h.label!=null){l=h.label;c='<span class="hidden-phone"> - '+h.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+l+"</a>"+c+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(a)}m.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var f=[];var h=j.filteredTaints;for(var n in h){var c=h[n];var k=-1;for(var e in c){var l=c[e];for(var g in f){if(g!=k&&$.inArray(e,f[g])){if(k>=0){var d=f.splice(k,1);var m=f.splice(g,1);f.push(d.concat(m))}else{f[g].push(n)}k=g}if(k>=0){break}}}if(k==-1){f.push([n])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};