function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var e=$("#active-addresses");var k=e.find("table");k.find("tbody").remove();for(var f in g){var m=g[f];if(f==0){k.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(k);for(var b in m){var c=m[b];var d=addresses[c];if(!d){continue}var l="";if(d.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var h=d.addr;if(d.label!=null){h=d.label;j='<span class="hidden-phone"> - '+d.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+h+"</a>"+j+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(a)}k.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var g=[];var l=e.filteredTaints;for(var d in l){var j=l[d];var n=-1;for(var m in j){var h=j[m];for(var f in g){if(f!=n&&$.inArray(m,g[f])){if(n>=0){var c=g.splice(n,1);var k=g.splice(f,1);g.push(c.concat(k))}else{g[f].push(d)}n=f}if(n>=0){break}}}if(n==-1){g.push([d])}}buildTable(g);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};