function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var n=$("#active-addresses");var g=n.find("table");g.find("tbody").remove();for(var b in l){var a=l[b];if(b==0){g.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(g);for(var h in a){var e=a[h];var d=addresses[e];if(!d){continue}var c="";if(d.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var k=d.addr;if(d.label!=null){k=d.label;f='<span class="hidden-phone"> - '+d.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+k+"</a>"+f+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(m)}g.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var l=[];var h=m.filteredTaints;for(var g in h){var k=h[g];var j=-1;for(var n in k){var e=k[n];for(var d in l){if(d!=j&&$.inArray(n,l[d])){if(j>=0){var f=l.splice(j,1);var c=l.splice(d,1);l.push(f.concat(c))}else{l[d].push(g)}j=d}if(j>=0){break}}}if(j==-1){l.push([g])}}buildTable(l);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};